def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:GetECGOutcomeResponder:1'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:3'

def ECGOutcome = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetECGOutcomeResponse/urn:ecgOutcome')

assert ECGOutcome.size() > 0

ECGOutcome.each {l ->
    def ECGOutcomeHolder = new XmlHolder( l )
    holder.namespaces.each {key, value -> ECGOutcomeHolder.namespaces[key] = value}

    def patientId = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:patientId/urn1:id')
    def patientIdType = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:patientId/urn1:type')
    assert patientId == context.patientId
    assert patientIdType == context.patientIdType
}

