import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:GetECGOutcomeResponder:1'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:3'

def ECGOutcomes = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetECGOutcomeResponse/urn:ecgOutcome')
def offending = []

assert ECGOutcomes.size() > 0

ECGOutcomes.each { doc ->
    def ECGOutcomeHolder = new XmlHolder( doc )
    holder.namespaces.each {key, value -> ECGOutcomeHolder.namespaces[key] = value}

    def documentId = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:documentId')
    def authorTime = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:accountableHealthcareProfessional/urn1:authorTime')
    authorTime = authorTime.substring(0, 8)
    def signatureTime = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:legalAuthenticator/urn1:signatureTime')
    signatureTime = signatureTime == null ? null : signatureTime.substring(0, 8)
    def ECGOutcomeTime = ECGOutcomeHolder.getNodeValue('urn:ecgOutcome/urn1:ecgOutcomeBody/urn1:resultTime')
    ECGOutcomeTime = ECGOutcomeTime == null ? null : ECGOutcomeTime.substring(0, 8)
    if ((context.timePeriodStart > authorTime || authorTime > context.timePeriodEnd) &&
            (signatureTime == null || context.timePeriodStart > signatureTime || signatureTime > context.timePeriodEnd) &&
            (ECGOutcomeTime == null || context.timePeriodStart > ECGOutcomeTime || ECGOutcomeTime > context.timePeriodEnd)) {
        offending.add(documentId)
    }
}

import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:GetECGOutcomeResponder:1'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:3'

def documentIds = holder.getNodeValues('/soapenv:Envelope/soapenv:Body/urn:GetECGOutcomeResponse/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:documentId') as List
def expectedDocumentIds = context.expectedDocumentIds ? context.expectedDocumentIds.tokenize(',') : []
def unexpectedDocumentIds = context.unexpectedDocumentIds ? context.unexpectedDocumentIds.tokenize(',') : []

log.debug("Assert documentIds, unexpectedDocumentIds:$unexpectedDocumentIds,\nexpectedDocumentIds, $expectedDocumentIds,\nfound documentIds $documentIds")

def missingExpectedIds = []
def extraUnexpectedIds = []

documentIds.each {id ->
    if (unexpectedDocumentIds.contains(id)) {
        extraUnexpectedIds.add(id)
    }
}

expectedDocumentIds.each {id ->
    if (!documentIds.contains(id)) {
        missingExpectedIds.add(id)
    }
}

if(!missingExpectedIds.isEmpty() || !extraUnexpectedIds.isEmpty()) {
	log.error("Response contains unexpected DocumentIds: $extraUnexpectedIds and/or does not contain  expected DocumentIds: $missingExpectedIds")
	assert(false)
}

