def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def responseHolder = new XmlHolder( messageExchange.response.responseContent )
responseHolder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
responseHolder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:GetECGOutcomeResponder:1'
responseHolder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:3'
def ecg = responseHolder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetECGOutcomeResponse/urn:ecgOutcome')

assert ecg.size() != 0

ecg.each {l ->
    def ECGOutcomeHolder = new XmlHolder( l )
    responseHolder.namespaces.each {key, value -> ECGOutcomeHolder.namespaces[key] = value}

    def sourceSystemHSAId = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:sourceSystemHSAId')
    def careContactId = ECGOutcomeHolder.getNodeValue('/urn:ecgOutcome/urn1:ecgOutcomeHeader/urn1:careContactId')

    assert sourceSystemHSAId == context.sourceSystemHSAId
    assert careContactId == context.careContactId1 || careContactId == context.careContactId2
}

