	new File(utils.projectPath + "/constraints.xml"))

failures.each { failure -> contextHelper.fail(failure.text) }

import com.eviware.soapui.support.XmlHolder
import se.skl.rivta.testsupport.soapui.ContextHelper
import com.eviware.soapui.support.GroovyUtils

def utils = new GroovyUtils(context)
def contextHelper = new ContextHelper(context)

def holder = new XmlHolder( messageExchange.response.responseContent )
holder.namespaces['soapenv'] = 'http://schemas.xmlsoap.org/soap/envelope/'
holder.namespaces['urn'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:GetMaternityMedicalHistoryResponder:3'
holder.namespaces['urn1'] = 'urn:riv:clinicalprocess:healthcond:actoutcome:3'

def records = holder.getDomNodes('/soapenv:Envelope/soapenv:Body/urn:GetMaternityMedicalHistoryResponse/urn:maternityMedicalRecord')

assert records.size() != 0

records.each {c ->
    def recordHolder = new XmlHolder( c )
    holder.namespaces.each {key, value -> recordHolder.namespaces[key] = value}


    def documentId = recordHolder.getNodeValue('/urn:maternityMedicalRecord/urn1:maternityMedicalRecordHeader/urn1:documentId')
    def documentTime = recordHolder.getNodeValue('/urn:maternityMedicalRecord/urn1:maternityMedicalRecordHeader/urn1:documentTime')
    def authorTime = recordHolder.getNodeValue('/urn:maternityMedicalRecord/urn1:maternityMedicalRecordHeader/urn1:accountableHealthcareProfessional/urn1:authorTime')
    def signatureTime = recordHolder.getNodeValue('/urn:maternityMedicalRecord/urn1:maternityMedicalRecordHeader/urn1:legalAuthenticator/urn1:signatureTime')

    if (context.timePeriodStart <= documentTime[0..7] && documentTime[0..7] <= context.timePeriodEnd) {
        return
    }
    if (context.timePeriodStart <= authorTime[0..7] && authorTime[0..7] <= context.timePeriodEnd) {
        return
    }
    if (signatureTime != null && context.timePeriodStart <= signatureTime[0..7] && signatureTime[0..7] <= context.timePeriodEnd) {
        return
    }

    contextHelper.fail("Response contains a record outside given date range ${context.timePeriodStart} - ${context.timePeriodEnd}. DocumentId: $documentId")
}

